import React, { useState } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { addPost } from "../../actions/post";

const PostForm = ({ addPost }) => {
  const [text, setText] = useState("");
  const [file, setFile] = useState("");

  return (
    <div className="post-form">
      <div className="small"></div>
      <form
        className="form my-1 form-actu"
        onSubmit={e => {
          e.preventDefault();
          addPost({ text });
          setText("");
          setFile("");
        }}
      >
        <textarea
          className="inputpost"
          name="text"
          cols="30"
          rows="5"
          placeholder="Partagez une pensÃ©e..."
          value={text}
          onChange={e => setText(e.target.value)}
          required
        />
        <input className="btn btn-primary" type='file' name='image' value={file} onChange= {e => setFile(file[0])}/>
        <input type='submit' name='image' value={file} onChange= {e => setFile(file[0])}/>
        {/* <div type="submit" className="btn btn-primary">
          <i class="far fa-paper-plane"></i>
        </div> */}
      </form>
    </div>
  );
};

PostForm.propTypes = {
  addPost: PropTypes.func.isRequired
};

export default connect(
  null,
  { addPost }
)(PostForm);
